

$(document).ready(function(){
  $("#submit").click(function(){
    var blaAHA = $('#txt_name').val();
    var title = $('#title_name').val();

    $("#mainName").html("<h1>" + title + "</h1>");


    function splitStr(str) {

	var string = str.split(" ");

	return string;
   }


    for (bla of splitStr(blaAHA)) {
    	    if (bla != "") {
		    var txt1 = `<img src="${bla}.png">`;
		    if (bla.includes("2")) {
		   	var txt1 = txt1.replace('2','');
		    	$("body").append(txt1);
		    	$("body").append(txt1);
		   } else { 
		   	 $("body").append(txt1);
		   }
	  }
    }

  });
  $("#clear").click(function(){

    
     $("#txt_name").val("");
     $("#title_name").val("");
     
	var images = document.getElementsByTagName('img');
	var l = images.length;
	for (var i = 0; i < l; i++) {
	    images[0].parentNode.removeChild(images[0]);
	}

  });
  
});
